export default Home

const PressableCard = ({side,imgSrc,text}:{side:string,imgSrc:string,text:string}) => {
    return(
        <View style={[{flex:1,justifyContent:'center',width:"100%"},side=="left"?{alignItems:'flex-end'}:{alignItems:'flex-start'}]}>
        <Pressable style={[{alignItems:'center',flexDirection:'row',backgroundColor:"#2a2a2a",height:55,borderRadius:3,width:"90%"},side=="left"?{marginRight:5}:{marginLeft:5}]}>
                <Image source={{uri:imgSrc}} style={{height:55,width:55,borderTopLeftRadius:3,borderBottomLeftRadius:3}}/>
                <Text style={{color:'white',fontSize:12,fontFamily:'GothamMedium',paddingLeft:10,paddingRight:5,flex:1,lineHeight: 15}} numberOfLines={2}>{text}</Text>
        </Pressable>
        </View>
    )
}

const HorizontalCards = ({imgSrc,text,subText,artist,first,thisIs,navigation}:{imgSrc:string,text:string,subText:string,artist:boolean,first:boolean,thisIs:boolean,navigation:any}) => {

    const getColors =  async () => {
        const result = await ImageColors.getColors(imgSrc!, {
            fallback: '#228B22',
            cache: true,
            key: 'unique_key',
          })
        return result.platform=="android"?result.vibrant:"#000000"
    }
    return(
        <View style={{marginTop:20,backgroundColor:'transparent'}}>
            <Pressable onPress={()=>{
                getColors().then((result)=>{navigation.navigate("Player",{image:imgSrc,color:result})})}}
            style={[{flex:1,paddingRight:8},first?{paddingLeft:0}:{paddingLeft:8}]}>
                    <View style={{flex:1,backgroundColor:'transparent'}}>
                    <Image source={{uri:imgSrc}} style={[{flex:1,height:150,width:150},artist?{borderRadius:75}:{borderRadius:0}]}/>
                    </View>
                    <View style={{height:50,justifyContent:'flex-start',marginTop:15,width:150}}>
                        {!thisIs?<Text style={[{color:'white',fontSize:12,fontFamily:'GothamMedium'},artist?{textAlign:'center'}:{textAlign:'left'}]} >{text}</Text>:<></>}
                        {!artist?<Text style={{color:'white',opacity:0.5,fontSize:12,fontFamily:'GothamMedium',flex:1}} numberOfLines={2}>{subText}</Text>:<></>}
                    </View>
            </Pressable>
        </View>
    )
}